pub fn get_banner_toml() -> String {
  let version = env!("CARGO_PKG_VERSION");

  format!(
    r#"
# @generated by
#     __             _____     _                 ___ ___
#  _ |  |___ ___ ___|   __|___| |_ ___ _____  __| | |_  |
# | |_| |_ -| . |   |__   |  _|   | -_|     ||. |_  |  _|
# |_____|___|___|_|_|_____|___|_|_|___|_|_|_|___| |_|___|
#  v{:20}         -- www.JsonSchema42.org
"#,
    version
  )
  .trim()
  .to_string()
}

